const burger=document.getElementById("burger"),nav=document.getElementById("nav"),body=document.body;function showMenu(){burger.classList.add("active"),nav.classList.add("active"),body.classList.add("no-scroll")}function closeMenu(){burger.classList.remove("active"),nav.classList.remove("active"),body.classList.remove("no-scroll")}burger.addEventListener("click",e=>{(burger.classList.contains("active")?closeMenu:showMenu)()});